import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import "../styles/PageListArticles.css";
/* import sections from "../data/Sections"; */
/* import Articles from "../data/Articles"; */

const ListArticles = () => {
  let { id } = useParams();
  const [sections, setSections] = useState([]);
  const [articles, setArticles] = useState([]);
  const [urlArticles, setUrlArticles] = useState("");

  useEffect(() => {
    const fechAllSections = async () => {
      try {
        const res = await axios.get("http://localhost:8000/sections");
        setSections(res.data);
        console.log(res.data);
      } catch (error) {
        console.log(error);
      }
    };
    fechAllSections();
  }, []);

  setUrlArticles(sections[0].articles);

  useEffect(() => {
    const fechAllArticles = async () => {
      try {
        const res = await axios.get(urlArticles);
        setArticles(res.data);
        console.log(res.data);
      } catch (error) {
        console.log(error);
      }
    };
    fechAllArticles();
  }, []);

  return (
    <div>
      <h1 className="titleSection">{sections[id].topic}</h1>
      <div className="description-container">
        <h2 className="descriptionSection">{sections[id].explanation}</h2>
      </div>
      <div className="articles-container">
        <div className="wrapper">
          {articles.map((article) => (
            <Link
              className="link-article"
              to={"/article/" + article.id + sections[id].topic}
            >
              <div className="article" key={article.id}>
                {article.cover && (
                  <img
                    className="cover-article"
                    src={article.cover}
                    alt="cover"
                  />
                )}
                <div className="text-container">
                  <p className="type-article text-mod">{article.section}</p>
                  <h3 className="title-article text-mod">{article.title}</h3>
                  <p className="date-article text-mod">{article.date}</p>
                </div>
              </div>
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ListArticles;
