import React, { useEffect, useState } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import sections from "../data/Sections";
import articles from "../data/Articles";
import "../styles/Home.css";

function SampleArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      style={{
        ...style,
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        background: "#f3dc17",
        borderRadius: "12px",
        width: "22px",
        height: "22px",
      }}
      onClick={onClick}
    />
  );
}

function Home() {
  const settings = {
    dots: true,
    infinite: true,
    speed: 2500,
    slidesToShow: 1,
    slidesToScroll: 1,
    nextArrow: <SampleArrow />,
    prevArrow: <SampleArrow />,
    autoplay: true,
    autoplaySpeed: 2500,
    cssEase: "linear",
  };

  const [frontPageArt, setFrontPageArt] = useState([]);
  const [newArt, setNewArt] = useState([]);
  const [dontMissArt, setDontMissArt] = useState({});
  const [seeListenArt, setSeeListenArt] = useState([]);
  const [drinksFoodArt, setDrinksFoodArt] = useState({});
  const [wellnessArt, setWellnessArt] = useState([]);
  const [fashionArt, setFashion] = useState([]);

  const fetchData = () => {
    const frontPageArtRouteAPI = "http://localhost:8000/accion";
    const newArtRouteAPI = "http://localhost:8000/ver&escuchar";
    const dontMissArtRouteAPI = "http://localhost:8000/drinks&food";
    const seeListenArtRouteAPI = "http://localhost:8000/ver&escuchar";
    const drinksFoodArtRouteAPI = "http://localhost:8000/drinks&food";
    const wellnessArtRouteAPI = "http://localhost:8000/bienestar";
    const fashionArtRouteAPI = "http://localhost:8000/moda";

    const getFrontPageArt = axios.get(frontPageArtRouteAPI);
    const getNewArt = axios.get(newArtRouteAPI);
    const getDontMissArt = axios.get(dontMissArtRouteAPI);
