import React, { useEffect, useState } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { Link } from "react-router-dom";
import axios from "axios";
import "../styles/Home.css";

function SampleArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      style={{
        ...style,
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        background: "#f3dc17",
        borderRadius: "12px",
        width: "22px",
        height: "22px",
      }}
      onClick={onClick}
    />
  );
}

function Home() {
  const settings = {
    dots: true,
    infinite: true,
    fade: true,
    speed: 4000,
    slidesToShow: 1,
    slidesToScroll: 1,
    nextArrow: <SampleArrow />,
    prevArrow: <SampleArrow />,
    autoplay: true,
    autoplaySpeed: 4000,
    cssEase: "linear",
  };

  const [frontPageArt, setFrontPageArt] = useState([]);
  const [newArt, setNewArt] = useState([]);
  const [dontMissArt, setDontMissArt] = useState({});
  const [seeListenArt, setSeeListenArt] = useState([]);
  const [drinksFoodArt, setDrinksFoodArt] = useState({});
  const [wellnessArt, setWellnessArt] = useState([]);
  const [fashionArt, setFashion] = useState([]);

  const fetchData = () => {
    const frontPageArtRouteAPI = "http://unbuencomienzo.mx/portada";
    const newArtRouteAPI = "http://unbuencomienzo.mx/newarticles";
    const dontMissArtRouteAPI = "http://unbuencomienzo.mx/inclusivalatest";
    const seeListenArtRouteAPI = "http://unbuencomienzo.mx/ver&escucharlatest";
    const drinksFoodArtRouteAPI = "http://unbuencomienzo.mx/drinks&foodlatest";
    const wellnessArtRouteAPI = "http://unbuencomienzo.mx/bienestarlatest";
    const fashionArtRouteAPI = "http://unbuencomienzo.mx/modalatest";

    const getFrontPageArt = axios.get(frontPageArtRouteAPI);
    const getNewArt = axios.get(newArtRouteAPI);
    const getDontMissArt = axios.get(dontMissArtRouteAPI);
    const getSeeListenArt = axios.get(seeListenArtRouteAPI);
    const getDrinksFoodArt = axios.get(drinksFoodArtRouteAPI);
    const getWellnessArt = axios.get(wellnessArtRouteAPI);
    const getFashionArt = axios.get(fashionArtRouteAPI);

    axios
      .all([
        getFrontPageArt,
        getNewArt,
        getDontMissArt,
        getSeeListenArt,
        getDrinksFoodArt,
        getWellnessArt,
        getFashionArt,
      ])
      .then(
        axios.spread((...allData) => {
          const dataFrontPageArt = allData[0].data[0];
          const dataNewArt = allData[1].data;
          const dataDontMissArt = allData[2].data[0];
          const dataSeeListenArt = allData[3].data;
          const dataDrinksFoodArt = allData[4].data[0];
          const dataWellnessArt = allData[5].data;
          const dataFashionArt = allData[6].data;

          setFrontPageArt(dataFrontPageArt);
          setNewArt(dataNewArt);
          setDontMissArt(dataDontMissArt);
          setSeeListenArt(dataSeeListenArt);
          setDrinksFoodArt(dataDrinksFoodArt);
          setWellnessArt(dataWellnessArt);
          setFashion(dataFashionArt);
        })
      )
      .catch((errors) => {
        // react on errors.
        console.error(errors.message);
      });
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="home-body-container">
      {/* Section no.1 */}
      <div className="carousel-container">
        <Slider {...settings}>
          <div className="carousel-item">
            <div
              style={{
                backgroundImage: `url(${frontPageArt.cover})`,
              }}
              className="carousel-item-container"
            >
              <div className="presentation-front-page-container">
                <div className="presentation-front-page-container-in">
                  <p className="front-page-section">{frontPageArt.section}</p>
                  <h2 className="front-page-title">{frontPageArt.title}</h2>
                  <Link
                    to={"/article/" + frontPageArt.ref + frontPageArt.id}
                    className="front-page-link"
                  >
                    Ir a la portada
                  </Link>
                </div>
              </div>
            </div>
          </div>
          <div className="carousel-item">
            <div
              style={{
                backgroundImage: `url(${frontPageArt.imgtwo})`,
              }}
              className="carousel-item-container"
            >
              <div className="presentation-front-page-container">
                <div className="presentation-front-page-container-in">
                  <p className="front-page-section">{frontPageArt.section}</p>
                  <h2 className="front-page-title">{frontPageArt.titletwo}</h2>
                  <Link
                    to={"/article/" + frontPageArt.ref + frontPageArt.id}
                    className="front-page-link"
                  >
                    Ir a la portada
                  </Link>
                </div>
              </div>
            </div>
          </div>
          <div className="carousel-item">
            <div
              style={{
                backgroundImage: `url(${frontPageArt.imgthree})`,
              }}
              className="carousel-item-container"
            >
              <div className="presentation-front-page-container">
                <div className="presentation-front-page-container-in">
                  <p className="front-page-section">{frontPageArt.section}</p>
                  <h2 className="front-page-title">
                    {frontPageArt.titlethree}
                  </h2>
                  <Link
                    to={"/article/" + frontPageArt.ref + frontPageArt.id}
                    className="front-page-link"
                  >
                    Ir a la portada
                  </Link>
                </div>
              </div>
            </div>
          </div>
        </Slider>
      </div>
      {/* Section no.2 */}
      <div className="news-container">
        <div className="section-label">
          <p className="text-section-label">Lo más nuevo</p>
        </div>
      </div>
      <div className="articles-container">
        <div className="wrapper">
          {newArt.map((article) => (
            <Link
              className="link-article"
              to={"/article/" + article.ref + article.id}
            >
              <div className="article" key={article.id}>
                {article.cover && (
                  <img
                    className="cover-article"
                    src={article.cover}
                    alt="cover"
                  />
                )}
                <div className="text-container">
                  <p className="type-article text-mod">{article.section}</p>
                  <h3 className="title-article text-mod">{article.title}</h3>
                  <p className="date-article text-mod">{article.date}</p>
                </div>
              </div>
            </Link>
          ))}
        </div>
      </div>
      {/* Section no.3 */}
      <div className="news-container">
        <div className="section-label">
          <p className="text-section-label">No te pierdas...</p>
        </div>
      </div>
      <div className="dont-miss-container">
        <img className="dont-miss-img" src={dontMissArt.cover} alt="cover" />
        <div className="dont-miss-article-container">
          <p className="section-dont-miss-in">{dontMissArt.section}</p>
          <Link
            to={"/article/" + dontMissArt.ref + dontMissArt.id}
            className="link-article-in"
          >
            <h2 className="title-dont-miss-in">{dontMissArt.title}</h2>
          </Link>
          <p className="date-dont-miss-in">{dontMissArt.date}</p>
        </div>
      </div>
      {/* Section no.4 */}
      <div className="news-container">
        <div className="section-label">
          <p className="text-section-label">¿Qué ver y escuchar?</p>
        </div>
      </div>
      <div className="articles-container">
        <div className="wrapper">
          {seeListenArt.map((article) => (
            <Link
              className="link-article"
              to={"/article/" + article.ref + article.id}
            >
              <div className="article" key={article.id}>
                {article.cover && (
                  <img
                    className="cover-article"
                    src={article.cover}
                    alt="cover"
                  />
                )}
                <div className="text-container">
                  <p className="type-article text-mod">{article.section}</p>
                  <h3 className="title-article text-mod">{article.title}</h3>
                  <p className="date-article text-mod">{article.date}</p>
                </div>
              </div>
            </Link>
          ))}
        </div>
      </div>
      {/* Section no.5 */}
      <div className="news-container">
        <div className="section-label">
          <p className="text-section-label">Drinks & Food</p>
        </div>
      </div>
      <div className="dont-miss-container">
        <img className="dont-miss-img" src={drinksFoodArt.cover} alt="cover" />
        <div className="dont-miss-article-container">
          <p className="section-dont-miss-in">{drinksFoodArt.section}</p>
          <Link
            to={"/article/" + drinksFoodArt.ref + drinksFoodArt.id}
            className="link-article-in"
          >
            <h2 className="title-dont-miss-in">{drinksFoodArt.title}</h2>
          </Link>
          <p className="date-dont-miss-in">{drinksFoodArt.date}</p>
        </div>
      </div>
      {/* Section no.6 */}
      <div className="news-container">
        <div className="section-label">
          <p className="text-section-label">Bienestar</p>
        </div>
      </div>
      <div className="articles-container">
        <div className="wrapper">
          {wellnessArt.map((article) => (
            <Link
              className="link-article"
              to={"/article/" + article.ref + article.id}
            >
              <div className="article" key={article.id}>
                {article.cover && (
                  <img
                    className="cover-article"
                    src={article.cover}
                    alt="cover"
                  />
                )}
                <div className="text-container">
                  <p className="type-article text-mod">{article.section}</p>
                  <h3 className="title-article text-mod">{article.title}</h3>
                  <p className="date-article text-mod">{article.date}</p>
                </div>
              </div>
            </Link>
          ))}
        </div>
      </div>
      {/* Section no.7 */}
      <div className="news-container">
        <div className="section-label">
          <p className="text-section-label">Moda</p>
        </div>
      </div>
      <div className="articles-container">
        <div className="wrapper">
          {fashionArt.map((article) => (
            <Link
              className="link-article"
              to={"/article/" + article.ref + article.id}
            >
              <div className="article" key={article.id}>
                {article.cover && (
                  <img
                    className="cover-article"
                    src={article.cover}
                    alt="cover"
                  />
                )}
                <div className="text-container">
                  <p className="type-article text-mod">{article.section}</p>
                  <h3 className="title-article text-mod">{article.title}</h3>
                  <p className="date-article text-mod">{article.date}</p>
                </div>
              </div>
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Home;
