import React from "react";
import { useParams } from "react-router-dom";
import "../styles/PageListArticles.css";
import Tables from "../data/tables";
import Artitles from "../data/Articles";

function ListArticles() {
  let { id } = useParams();

  return (
    <div>
      <h1 className="titleSection">{Tables[id].topic}</h1>
      <div className="description-container">
        <h2 className="descriptionSection">{Tables[id].description}</h2>
      </div>
      <div className="articles">
        {Artitles.map((article) => (
          <div className="article" key={article.id}>
            {book.cover && <img src={article.cover} alt="cover" />}
            <h2>{article.type}</h2>
            <p>{article.title}</p>
            <span>{book.description}</span>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ListArticles;
