import React, { useEffect, useState } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import sections from "../data/Sections";
/* import { useNavigate } from "react-router-dom";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from "react-responsive-carousel"; */
import "../styles/Home.css";

function SampleArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      style={{
        ...style,
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        background: "#f3dc17",
        borderRadius: "12px",
        width: "22px",
        height: "22px",
      }}
      onClick={onClick}
    />
  );
}

function Home() {
  let { id } = useParams();
  const [articles, setArticles] = useState([]);
  var urlArticles = sections[id].section;

  const fechAllArticles = async () => {
    try {
      const res = await axios.get("http://localhost:8000/ver&excuchar");
      setArticles(res.data);
      console.log(res.data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    fechAllArticles();
  }, [id]);
  /* const navigate = useNavigate(); */
  const settings = {
    dots: true,
    infinite: true,
    speed: 2500,
    slidesToShow: 1,
    slidesToScroll: 1,
    nextArrow: <SampleArrow />,
    prevArrow: <SampleArrow />,
    autoplay: true,
    autoplaySpeed: 2500,
    cssEase: "linear",
  };

  return (
    <div className="home-body-container">
      <div className="carousel-container">
        <Slider {...settings}>
          <div className="carousel-item">
            <h3>1</h3>
          </div>
          <div className="carousel-item">
            <h3>2</h3>
          </div>
          <div className="carousel-item">
            <h3>3</h3>
          </div>
        </Slider>
      </div>
      <div className="news-container">
        <div className="section-label">
          <p className="text-section-label">Lo más nuevo</p>
        </div>
      </div>
      <div className="articles-container">
        <div className="wrapper">
          {articles.map((article) => (
            <Link
              className="link-article"
              to={"/article/" + sections[id].section + article.id}
            >
              <div className="article" key={article.id}>
                {article.cover && (
                  <img
                    className="cover-article"
                    src={article.cover}
                    alt="cover"
                  />
                )}
                <div className="text-container">
                  <p className="type-article text-mod">{article.section}</p>
                  <h3 className="title-article text-mod">{article.title}</h3>
                  <p className="date-article text-mod">{article.date}</p>
                </div>
              </div>
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Home;
