import React from "react";
import { useParams } from "react-router-dom";

function Article() {
  let { id } = useParams();

  const idArticle = id.replace(/[^0-9]+/g, "");
  const idTable = id.replace(/[0-9]+/g, "");

  const [article, setArticle] = useState([]);
  var urlArticles = sections[id].section;

  const fechAllArticles = async () => {
    try {
      const res = await axios.get(
        `http://localhost:8000/${idTable}/${idArticle}`
      );
      setArticle(res.data);
      console.log(res.data);
    } catch (error) {
      console.log(error);
    }
  };
  fechAllArticles();

  return (
    <div>
      Article {idArticle + " - " + idTable}
      <h2>{article[0].title}</h2>
      <h3>{article[0].explanation}</h3>
      <p>{article[0].article}</p>
    </div>
  );
}

export default Article;
