import React from "react";
import { useParams, Link } from "react-router-dom";
import "../styles/PageListArticles.css";
import Tables from "../data/tables";
import Artitles from "../data/Articles";

function ListArticles() {
  let { id } = useParams();

  return (
    <div>
      <h1 className="titleSection">{Tables[id].topic}</h1>
      <div className="description-container">
        <h2 className="descriptionSection">{Tables[id].description}</h2>
      </div>
      <div className="articles-container">
        <div className="wrapper">
          {Artitles.map((article) => (
            <Link
              className="link-article"
              to={"/article/" + { id: "hola", type: "jajaja" }}
            >
              <div className="article" key={article.id}>
                {article.cover && (
                  <img
                    className="cover-article"
                    src={article.cover}
                    alt="cover"
                  />
                )}
                <div className="text-container">
                  <p className="type-article text-mod">{article.type}</p>
                  <h3 className="title-article text-mod">{article.title}</h3>
                  <p className="date-article text-mod">{article.data}</p>
                </div>
              </div>
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
}

export default ListArticles;
