import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import parse from "html-react-parser";
import axios from "axios";

const Article = () => {
  let { id } = useParams();
  const [article, setArticle] = useState({});
  const [textHTML, setTextHTML] = useState("<p>Nothing</p>");

  const idArticle = id.replace(/[^0-9]+/g, "");
  const idTable = id.replace(/[0-9]+/g, "");

  console.log("http://localhost:8000/" + idTable + "/" + idArticle);

  const fechArticle = async () => {
    try {
      const res = await axios.get(
        "http://localhost:8000/" + idTable + "/" + idArticle
      );
      setArticle(res.data[0]);
      setTextHTML(res.data[0].article);
      console.log(res.data);
    } catch (error) {
      /* console.log(error); */
    }
  };

  useEffect(() => {
    fechArticle();
  }, [id]);

  window.scroll({
    top: 0,
    behavior: "smooth",
  });

  return (
    <div>
      <div className="article-head-container">
        <div className="img-article">
          <img src={article.cover} alt="cover" />
        </div>
        <div className="presentation-article">
          <h2>{article.title}</h2>
          <h3>{article.explanation}</h3>
        </div>
      </div>
      <div className="article-body-container">{parse(textHTML)}</div>
    </div>
  );
};

export default Article;
