import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import parse from "html-react-parser";
import axios from "axios";
import "../styles/PageArticle.css";
import data from "../data/Data";
import Helmet from "react-helmet";

const Article = () => {
  let { id } = useParams();
  const [article, setArticle] = useState({});
  const [textHTML, setTextHTML] = useState("<p>Nothing</p>");

  const idArticle = id.replace(/[^0-9]+/g, "");
  const idTable = id.replace(/[0-9]+/g, "");

  console.log(data.urlAPI + "/" + idTable + "/" + idArticle);

  const fechArticle = async () => {
    try {
      const res = await axios.get(
        data.urlAPI + "/" + idTable + "/" + idArticle
      );
      setArticle(res.data[0]);
      setTextHTML(res.data[0].article);
      console.log(res.data);
    } catch (error) {
      /* console.log(error); */
    }
  };

  useEffect(() => {
    fechArticle();
  }, [id]);

  window.scroll({
    top: 0,
    behavior: "smooth",
  });

  return (
    <div>
      <Helmet>
        {/* HTML Meta Tags */}
        <title>Daniel Gutierrez</title>
        <meta
          name="description"
          content="Vocalista del grupo de Rock Mexicano, La Gusana Ciega, compositor y productor con más de 20 años de experiencia en la industria de la música."
        />

        {/* Google / Search Engine Tags */}
        <meta itemprop="name" content="Daniel Gutierrez" />
        <meta
          itemprop="description"
          content="Vocalista del grupo de Rock Mexicano, La Gusana Ciega compositor y productor con más de 20 años de experiencia en la industria de la música."
        />
        <meta
          itemprop="image"
          content="https://www.sopitas.com/wp-content/uploads/2018/09/la-gusana-ciega.jpg?w=1024"
        />

        {/* Facebook Meta Tags */}
        <meta property="og:url" content="http://zagazine.mx/article/moda1" />
        <meta property="og:type" content="website" />
        <meta property="og:title" content="Daniel Gutierrez" />
        <meta
          property="og:description"
          content="Vocalista del grupo de Rock Mexicano, La Gusana Ciega, compositor y productor con más de 20 años de experiencia en la industria de la música."
        />
        <meta
          property="og:image"
          content="https://www.sopitas.com/wp-content/uploads/2018/09/la-gusana-ciega.jpg?w=1024"
        />

        {/* Twitter Meta Tags */}
        <meta name="twitter:card" content="summary_large_image" />
        <meta name="twitter:title" content="Daniel Gutierrez" />
        <meta
          name="twitter:description"
          content="Vocalista del grupo de Rock Mexicano, La Gusana Ciega, compositor y productor con más de 20 años de experiencia en la industria de la música."
        />
        <meta
          name="twitter:image"
          content="https://www.sopitas.com/wp-content/uploads/2018/09/la-gusana-ciega.jpg?w=1024"
        />
      </Helmet>
      <div className="article-head-container">
        <div className="img-article-container">
          <img className="img-article" src={article.cover} alt="cover" />
        </div>
        <div className="presentation-article-container">
          <p className="text-format section-article-in">{article.section}</p>
          <h2 className="text-format title-article-in">{article.title}</h2>
          <h3 className="text-format explanation-article-in">
            {article.explanation}
          </h3>
          <p className="text-format date-article-in">{article.date}</p>
          <div className="social-shared-link-container">
            <ul className="list-social-shared-link">
              <li className="social-link">
                <a
                  href={
                    "https://www.facebook.com/sharer/sharer.php?u=" +
                    window.location
                  }
                  className="social-link-a"
                >
                  <i className="ri-facebook-circle-line config-icon"></i>
                </a>
              </li>
              <li className="social-link">
                <a
                  href={
                    "https://pinterest.com/pin/create/button/?url=" +
                    window.location +
                    "&media=&description=" +
                    article.explanation
                  }
                  className="social-link-a"
                >
                  <i className="ri-pinterest-line config-icon"></i>
                </a>
              </li>
              <li className="social-link">
                <a
                  href={
                    "https://twitter.com/intent/tweet?url=" +
                    window.location +
                    "&text=" +
                    article.explanation
                  }
                  className="social-link-a"
                >
                  <i className="ri-twitter-line config-icon"></i>
                </a>
              </li>
            </ul>
          </div>
        </div>
      </div>
      <div className="container-body">
        <section className="article-body-container">{parse(textHTML)}</section>
      </div>
    </div>
  );
};

export default Article;
