import React, { useState, useEffect } from "react";
import axios from "axios";

function AboutUs() {
  const [aboutUs, setAboutUs] = useState({});

  const fechArticle = async () => {
    try {
      const res = await axios.get("http://localhost:8000/aboutus");
      setAboutUs(res.data[0]);
    } catch (error) {
      /* console.log(error); */
    }
  };

  useEffect(() => {
    fechArticle();
  }, []);

  window.scroll({
    top: 0,
    behavior: "smooth",
  });

  return (
    <div>
      <div className="slogan-container">
        <h1>{aboutUs.slogan}</h1>
      </div>
      <div className="mision-container">
        <p>{aboutUs.mision}</p>
      </div>
      <div className="vision-container">
        <p>{aboutUs.vision}</p>
      </div>
      <div className="editorial-container">
        <p>{aboutUs.editorial}</p>
      </div>
      <div className="contacto-container">
        <p>WhatsApp</p>
        <i className="ri-whatsapp-line config-icon">{aboutUs.whatsapp}</i>
        <i className="ri-phone-line config-icon">{aboutUs.telefono}</i>
      </div>
    </div>
  );
}

export default AboutUs;
