import "../styles/Home.css";

function SampleArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      style={{
        ...style,
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        background: "#f3dc17",
        borderRadius: "12px",
        width: "22px",
        height: "22px",
      }}
      onClick={onClick}
    />
  );
}

function Home() {
  /* const navigate = useNavigate(); */
  const settings = {
    dots: true,

    infinite: true,
    speed: 2000,
    slidesToShow: 1,
    slidesToScroll: 1,
    nextArrow: <SampleArrow />,
    prevArrow: <SampleArrow />,
    autoplay: true,
    autoplaySpeed: 2000,
    cssEase: "linear",
  };

  return (
    <div style={{ display: "flex", justifyContent: "center" }}>
