import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

function Article() {
  let { id } = useParams();
  const [article, setArticle] = useState([]);

  const idArticle = id.replace(/[^0-9]+/g, "");
  const idTable = id.replace(/[0-9]+/g, "");

  console.log("http://localhost:8000/" + idTable + "/" + idArticle);

  useEffect(() => {
    const fechArticle = async () => {
      try {
        const res = await axios.get("http://localhost:8000/ver&escuchar/1");
        setArticle(res.data);
        console.log(res.data);
      } catch (error) {
        /* console.log(error); */
      }
    };
    fechArticle();
  }, []);

  return (
    <div>
      <h2>{article[0].title}</h2>
      <h3>{article[0].explanation}</h3>
      <p>{article[0].article}</p>
    </div>
  );
}

export default Article;
